import React, { useState, useEffect } from "react";

const PopupDatSan = ({ san, onClose, onDat }) => {
  const [ten, setTen] = useState("");
  const [soDienThoai, setSoDienThoai] = useState("");
  const [ngay, setNgay] = useState(() => new Date().toISOString().split("T")[0]);
  const [gioBatDau, setGioBatDau] = useState("06:00");
  const [thoiLuong, setThoiLuong] = useState(1);
  const giaTheoGio = san.giaThue || 70000;

  // ‚úÖ T√≠nh gi·ªù k·∫øt th√∫c
  const tinhGioKetThuc = () => {
    const [startHour, startMinute] = gioBatDau.split(":").map(Number);
    const ketThucHour = startHour + thoiLuong;
    if (ketThucHour > 23) return "Kh√¥ng h·ª£p l·ªá";
    return `${ketThucHour.toString().padStart(2, "0")}:${startMinute.toString().padStart(2, "0")}`;
  };

  const gioKetThuc = tinhGioKetThuc();

  // ‚úÖ X√°c ƒë·ªãnh gi·ªù b·∫Øt ƒë·∫ßu hi·ªán t·∫°i (d·ª±a theo th·ªùi gian th·ª±c)
  const generateStartTimeOptions = () => {
    const now = new Date();
    const isToday = ngay === now.toISOString().split("T")[0];

    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();

    let minHour = 6;
    if (isToday) {
      if (currentMinute >= 15) {
        minHour = currentHour + 1;
      } else {
        minHour = currentHour;
      }
      if (minHour < 6) minHour = 6;
    }

    const options = [];
    for (let h = minHour; h <= 22; h++) {
      options.push(`${h.toString().padStart(2, "0")}:00`);
    }

    return options;
  };

  // ‚úÖ X·ª≠ l√Ω submit ƒë·∫∑t s√¢n
  const handleSubmit = () => {
    if (!ten.trim()) return alert("Vui l√≤ng nh·∫≠p t√™n ng∆∞·ªùi ƒë·∫∑t.");
    if (!soDienThoai.trim()) return alert("Vui l√≤ng nh·∫≠p s·ªë ƒëi·ªán tho·∫°i.");

    // ‚úÖ Ki·ªÉm tra s·ªë ƒëi·ªán tho·∫°i h·ª£p l·ªá (10 ch·ªØ s·ªë, 0-9)
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(soDienThoai.trim())) {
      return alert("S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá");
    }

    if (gioKetThuc === "Kh√¥ng h·ª£p l·ªá") {
      return alert("Th·ªùi gian k·∫øt th√∫c v∆∞·ª£t qu√° 23:00. Vui l√≤ng ch·ªçn l·∫°i.");
    }

    onDat({
      ten,
      soDienThoai,
      ngay,
      gioBatDau,
      gioKetThuc,
      thoiLuong,
      san,
      tongTien: giaTheoGio * thoiLuong,
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded w-full max-w-md">
        <h2 className="text-xl font-bold mb-4">ƒê·∫∑t s√¢n {san.ten}</h2>

        {/* Ng√†y ƒë·∫∑t */}
        <label className="block mt-2">Ng√†y ƒë·∫∑t:</label>
        <input
          type="date"
          className="w-full border p-2"
          value={ngay}
          min={new Date().toISOString().split("T")[0]}
          onChange={(e) => setNgay(e.target.value)}
        />

        {/* Gi·ªù b·∫Øt ƒë·∫ßu */}
        <label className="block mt-2">Gi·ªù b·∫Øt ƒë·∫ßu:</label>
        <select
          className="w-full border p-2"
          value={gioBatDau}
          onChange={(e) => setGioBatDau(e.target.value)}
        >
          {generateStartTimeOptions().map((gio) => (
            <option key={gio} value={gio}>
              {gio}
            </option>
          ))}
        </select>

        {/* Th·ªùi l∆∞·ª£ng */}
        <label className="block mt-2">Th·ªùi l∆∞·ª£ng (gi·ªù):</label>
        <input
          type="number"
          className="w-full border p-2"
          min={1}
          max={23 - parseInt(gioBatDau.split(":")[0])}
          value={thoiLuong}
          onChange={(e) => setThoiLuong(Number(e.target.value))}
        />

        {/* K·∫øt th√∫c + t·ªïng ti·ªÅn */}
        <div className="mt-2 text-sm text-gray-600">
          ‚è± Gi·ªù k·∫øt th√∫c: <strong>{gioKetThuc}</strong> <br />
          üí∞ T·ªïng ti·ªÅn: <strong>{(giaTheoGio * thoiLuong).toLocaleString()}ƒë</strong>
        </div>

        {/* T√™n ng∆∞·ªùi ƒë·∫∑t */}
        <input
          type="text"
          placeholder="T√™n ng∆∞·ªùi ƒë·∫∑t"
          value={ten}
          onChange={(e) => setTen(e.target.value)}
          className="w-full border p-2 mt-4"
        />

        {/* S·ªë ƒëi·ªán tho·∫°i */}
        <input
          type="tel"
          placeholder="S·ªë ƒëi·ªán tho·∫°i"
          value={soDienThoai}
          onChange={(e) => setSoDienThoai(e.target.value)}
          className="w-full border p-2 mt-2"
        />

        {/* N√∫t */}
        <div className="flex justify-end gap-4 mt-4">
          <button onClick={onClose} className="text-gray-600">Hu·ª∑</button>
          <button
            className="bg-black text-white px-4 py-2 rounded"
            onClick={handleSubmit}
          >
            ƒê·∫∑t s√¢n
          </button>
        </div>
      </div>
    </div>
  );
};

export default PopupDatSan;
