import React, { useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

const dummyData = [
  {
    ten: "Sân 1",
    giaThue: 70000,
    thoiGian: [
      { gioBatDau: "06:00", gioKetThuc: "07:00", trangThai: "trong" },
      { gioBatDau: "07:00", gioKetThuc: "08:00", trangThai: "daDat" },
    ],
  },
  {
    ten: "Sân 2",
    giaThue: 80000,
    thoiGian: [
      { gioBatDau: "06:00", gioKetThuc: "07:00", trangThai: "trong" },
      { gioBatDau: "07:00", gioKetThuc: "08:00", trangThai: "trong" },
    ],
  },
];

const Home = () => {
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);

  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot });
    setPopup(true);
  };

  const handleDatSan = (data) => {
    console.log("Dữ liệu đặt sân:", data);
    localStorage.setItem("datSan", JSON.stringify(data));
    setPopup(false);
    alert("Đặt sân thành công (demo)!");
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Đặt Sân Cầu Lông Victor</h1>
      <SanGrid danhSachSan={dummyData} onClickSlot={handleClickSlot} />
      <FooterMap />
      {popup && (
        <PopupDatSan
          san={selected.san}
          slot={selected.slot}
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
