  const [ngay, setNgay] = useState(() => new Date().toISOString().split("T")[0]);
  const [gioBatDau, setGioBatDau] = useState("06:00");
  const [thoiLuong, setThoiLuong] = useState(1);
  const giaTheoGio = san.giaThue || 70000;

  // ‚úÖ H√†m t√≠nh gi·ªù k·∫øt th√∫c ƒë√∫ng
const tinhGioKetThuc = () => {
    const [startHour, startMinute] = gioBatDau.split(":").map(Number);
    const ketThucHour = startHour + thoiLuong;
    if (ketThucHour > 23) return "Kh√¥ng h·ª£p l·ªá";
    return `${ketThucHour.toString().padStart(2, "0")}:${startMinute.toString().padStart(2, "0")}`;
  };

