    const options = [];
    for (let h = 6; h <= 22; h++) {
      if (!isToday || h >= currentHour) {
        const label = `${h.toString().padStart(2, "0")}:00`;
        options.push(label);
      }
    }
    return options;
  };

  const handleSubmit = () => {
    if (!ten || !soDienThoai || !ngay) {
