import React, { useState } from "react";

const PopupDatSan = ({ san, slot, onClose, onDat }) => {
  const [ten, setTen] = useState("");
  const [soDienThoai, setSoDienThoai] = useState("");
  const [thoiLuong, setThoiLuong] = useState(1);
  const giaTheoGio = san.giaThue || 70000;

  const handleSubmit = () => {
    if (!ten || !soDienThoai) return;
    onDat({ ten, soDienThoai, san, slot, thoiLuong });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded w-full max-w-md">
        <h2 className="text-xl font-bold mb-4">Đặt sân {san.ten}</h2>
        <p>
          Thời gian: {slot.gioBatDau} - {slot.gioKetThuc}
        </p>
        <input
          type="text"
          placeholder="Tên người đặt"
          value={ten}
          onChange={(e) => setTen(e.target.value)}
          className="w-full border p-2 mt-4"
        />
        <input
          type="tel"
          placeholder="Số điện thoại"
          value={soDienThoai}
          onChange={(e) => setSoDienThoai(e.target.value)}
          className="w-full border p-2 mt-2"
        />
        <select
          value={thoiLuong}
          onChange={(e) => setThoiLuong(parseInt(e.target.value))}
          className="w-full border p-2 mt-2"
        >
          {[1, 2, 3].map((h) => (
            <option key={h} value={h}>
              {h} giờ - {(giaTheoGio * h).toLocaleString()}đ
            </option>
          ))}
        </select>
        <div className="flex justify-end gap-4 mt-4">
          <button onClick={onClose}>Huỷ</button>
          <button
            className="bg-black text-white px-4 py-2 rounded"
            onClick={handleSubmit}
          >
            Đặt sân
          </button>
        </div>
      </div>
    </div>
  );
};

export default PopupDatSan;
