import React, { useEffect, useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

// ‚öôÔ∏è C·∫•u h√¨nh
const GIO_MO = 6;
const GIO_DONG = 23;
const MAX_NGAY = 30;

// üß† Helpers
const toMinutes = (time) => {
  const [h, m] = time.split(":").map(Number);
  return h * 60 + m;
};
const toTime = (minutes) => {
  const h = Math.floor(minutes / 60).toString().padStart(2, "0");
  return `${h}:00`;
};
const congNgay = (ngayISO, soNgay) => {
  const d = new Date(ngayISO);
  d.setDate(d.getDate() + soNgay);
  return d.toISOString().split("T")[0];
};

// üì¶ LocalStorage
const LAY_DATA_LS = () => {
  try {
    return JSON.parse(localStorage.getItem("danhSachSan")) || [];
  } catch {
    return [];
  }
};
const LUU_DATA_LS = (data) => {
  localStorage.setItem("danhSachSan", JSON.stringify(data));
};

// üîÅ T√≠nh slot tr·ªëng
const tinhSlotTrong = (daDat, ngay) => {
  const start = GIO_MO * 60;
  const end = GIO_DONG * 60;

  const datHomNay = (daDat || []).filter((d) => d.ngay === ngay);
  const datMins = datHomNay
    .map((d) => ({
      start: toMinutes(d.gioBatDau),
      end: toMinutes(d.gioKetThuc),
    }))
    .sort((a, b) => a.start - b.start);

  const slots = [];
  let current = start;

  for (const d of datMins) {
    if (d.start > current) {
      slots.push({
        gioBatDau: toTime(current),
        gioKetThuc: toTime(d.start),
        trangThai: "trong",
      });
    }
    current = Math.max(current, d.end);
  }

  if (current < end) {
    slots.push({
      gioBatDau: toTime(current),
      gioKetThuc: toTime(end),
      trangThai: "trong",
    });
  }

  return slots;
};

const Home = () => {
  const [danhSachSan, setDanhSachSan] = useState([]);
  const [ngayChon, setNgayChon] = useState(() =>
    new Date().toISOString().split("T")[0]
  );
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);

  // 1Ô∏è‚É£ Load t·ª´ localStorage
  useEffect(() => {
    const data = LAY_DATA_LS();
    if (data.length > 0) {
      setDanhSachSan(data);
    } else {
      setDanhSachSan([
        { ten: "S√¢n 1", giaThue: 70000, daDat: [] },
        { ten: "S√¢n 2", giaThue: 80000, daDat: [] },
      ]);
    }
  }, []);

  // 2Ô∏è‚É£ L∆∞u khi thay ƒë·ªïi
  useEffect(() => {
    LUU_DATA_LS(danhSachSan);
  }, [danhSachSan]);

  // 3Ô∏è‚É£ Auto chuy·ªÉn ng√†y n·∫øu kh√¥ng c√≥ slot
  useEffect(() => {
    const kiemTraCoSlot = (ngay) => {
      for (const san of danhSachSan) {
        const slots = tinhSlotTrong(san.daDat, ngay);
        if (slots.length > 0) return true;
      }
      return false;
    };

    let ngay = ngayChon;
    let dem = 0;
    while (!kiemTraCoSlot(ngay) && dem < MAX_NGAY) {
      ngay = congNgay(ngay, 1);
      dem++;
    }

    if (ngay !== ngayChon) {
      setNgayChon(ngay); // c·∫≠p nh·∫≠t ng√†y m·ªõi c√≥ slot
    }
  }, [ngayChon, danhSachSan]);

  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot });
    setPopup(true);
  };

  const handleDatSan = (data) => {
    const capNhat = [...danhSachSan];
    const idx = capNhat.findIndex((s) => s.ten === data.san.ten);
    if (idx === -1) return;

    capNhat[idx].daDat.push({
      ngay: data.ngay,
      gioBatDau: data.gioBatDau,
      gioKetThuc: data.gioKetThuc,
    });

    setDanhSachSan(capNhat);
    setPopup(false);
    alert("‚úÖ ƒê·∫∑t s√¢n th√†nh c√¥ng!");
  };

  const danhSachSanRender = danhSachSan.map((san) => ({
    ...san,
    thoiGian: tinhSlotTrong(san.daDat, ngayChon),
  }));

  return (
    <div className="p-6 max-w-4xl mx-auto bg-blur rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">ƒê·∫∑t S√¢n Victor üè∏</h1>

      <label className="block font-medium mb-2">Ch·ªçn ng√†y:</label>
      <input
        type="date"
        className="border p-2 mb-4 w-full"
        value={ngayChon}
        min={new Date().toISOString().split("T")[0]}
        max={congNgay(new Date().toISOString().split("T")[0], MAX_NGAY)}
        onChange={(e) => setNgayChon(e.target.value)}
      />

      <SanGrid
        danhSachSan={danhSachSanRender}
        ngayChon={ngayChon}
        onClickSlot={handleClickSlot}
      />

      <FooterMap />

      {popup && selected && (
        <PopupDatSan
          san={selected.san}
          slot={selected.slot}
          ngayMacDinh={ngayChon}
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
