import React, { useState, useEffect } from "react";
import ThanhToanPopup from "./ThanhToanPopup";

const PopupDatSan = ({ san, slot, onClose, onDat }) => {
  const [ten, setTen] = useState("");
  const [soDienThoai, setSoDienThoai] = useState("");
  const [ngay, setNgay] = useState(() => new Date().toISOString().split("T")[0]);
  const [gioBatDau, setGioBatDau] = useState("06:00");
  const [thoiLuong, setThoiLuong] = useState(1);
  const [gioKetThuc, setGioKetThuc] = useState("07:00");
  const [showThanhToan, setShowThanhToan] = useState(false);
  const giaTheoGio = san.giaThue || 70000;

  // Gán slot khi mở popup
  useEffect(() => {
    if (slot?.gioBatDau) {
      setGioBatDau(slot.gioBatDau);
    }
  }, [slot]);

  // Tính giờ kết thúc
  useEffect(() => {
    const [startHour] = gioBatDau.split(":").map(Number);
    const ketThucHour = startHour + thoiLuong;

    if (ketThucHour > 23) {
      setGioKetThuc("Không hợp lệ");
    } else {
      setGioKetThuc(`${ketThucHour.toString().padStart(2, "0")}:00`);
    }
  }, [gioBatDau, thoiLuong]);

  // Tính các option giờ bắt đầu hợp lệ
  const generateStartTimeOptions = () => {
    const now = new Date();
    const isToday = ngay === now.toISOString().split("T")[0];
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();

    let minHour = 6;
    if (isToday) {
      minHour = currentMinute >= 15 ? currentHour + 1 : currentHour;
      if (minHour < 6) minHour = 6;

      const options = [];
      for (let h = minHour; h <= 22; h++) {
        options.push(`${h.toString().padStart(2, "0")}:00`);
      }
      return options;
    }

    // Nếu không phải hôm nay, show full từ 6h đến 22h
    return Array.from({ length: 17 }, (_, i) => {
      const h = 6 + i;
      return `${h.toString().padStart(2, "0")}:00`;
    });
  };

  // Kiểm tra và mở popup thanh toán
  const handleMoThanhToan = () => {
    if (!ten.trim()) return alert("Vui lòng nhập tên người đặt.");
    if (!soDienThoai.trim()) return alert("Vui lòng nhập số điện thoại.");
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(soDienThoai.trim()))
      return alert("Số điện thoại không hợp lệ. Vui lòng nhập đúng 10 chữ số.");
    if (gioKetThuc === "Không hợp lệ")
      return alert("Thời gian kết thúc vượt quá 23:00. Vui lòng chọn lại.");

    setShowThanhToan(true);
  };

  // Dữ liệu gửi sang popup thanh toán
  const duLieuDatSan = {
    ten,
    soDienThoai,
    ngay,
    gioBatDau,
    gioKetThuc,
    thoiLuong,
    san,
    tongTien: giaTheoGio * thoiLuong,
  };

  return (
    <>
      <div
        className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center"
        onMouseDown={onClose}
      >
        <div
          className="bg-white p-6 rounded w-full max-w-md"
          onMouseDown={(e) => e.stopPropagation()}
        >
          <h2 className="text-xl font-bold mb-4">Đặt sân {san.ten}</h2>

          <label className="block mt-2">Ngày đặt:</label>
          <input
            type="date"
            className="w-full border p-2"
            value={ngay}
            min={new Date().toISOString().split("T")[0]}
            onChange={(e) => setNgay(e.target.value)}
          />

          <label className="block mt-2">Giờ bắt đầu:</label>
          <select
            className="w-full border p-2"
            value={gioBatDau}
            onChange={(e) => setGioBatDau(e.target.value)}
          >
            {generateStartTimeOptions().map((gio) => (
              <option key={gio} value={gio}>
                {gio}
              </option>
            ))}
          </select>

          <label className="block mt-2">Thời lượng (giờ):</label>
          <input
            type="number"
            className="w-full border p-2"
            min={1}
            max={23 - parseInt(gioBatDau.split(":")[0])}
            value={thoiLuong}
            onChange={(e) => setThoiLuong(Number(e.target.value))}
          />

          <div className="mt-2 text-sm text-gray-600">
            ⏱ Giờ kết thúc: <strong>{gioKetThuc}</strong> <br />
            💰 Tổng tiền: <strong>{(giaTheoGio * thoiLuong).toLocaleString()}đ</strong>
          </div>

          <input
            type="text"
            placeholder="*Tên người đặt"
            value={ten}
            onChange={(e) => setTen(e.target.value)}
            className="w-full border p-2 mt-4"
          />
          <input
            type="tel"
            placeholder="* Số điện thoại (Zalo)"
            value={soDienThoai}
            onChange={(e) => setSoDienThoai(e.target.value)}
            className="w-full border p-2 mt-2"
          />

          <div className="flex justify-end gap-4 mt-4">
            <button onClick={onClose} className="text-gray-600">
              Huỷ
            </button>
            <button
              className="bg-black text-white px-4 py-2 rounded"
              onClick={handleMoThanhToan}
            >
              Đặt sân
            </button>
          </div>
        </div>
      </div>

      {showThanhToan && (
        <ThanhToanPopup
          thongTinDatSan={duLieuDatSan}
          onXacNhanThanhToan={(data) => {
            onDat(data);
            setShowThanhToan(false);
            onClose();
          }}
          onHuy={() => setShowThanhToan(false)}
        />
      )}
    </>
  );
};

export default PopupDatSan;
