import React, { useEffect, useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

const GIO_MO = 6;
const GIO_DONG = 23;

const LAY_DATA_LS = () => {
  try {
    return JSON.parse(localStorage.getItem("danhSachSan")) || [];
  } catch {
    return [];
  }
};

const LUU_DATA_LS = (data) => {
  localStorage.setItem("danhSachSan", JSON.stringify(data));
};

const toMinutes = (time) => {
  const [h, m] = time.split(":").map(Number);
  return h * 60 + m;
};

const toTime = (minutes) => {
  const h = Math.floor(minutes / 60).toString().padStart(2, "0");
  return `${h}:00`;
};

const tinhSlotTrong = (daDat) => {
  const start = GIO_MO * 60;
  const end = GIO_DONG * 60;

  const datMins = daDat
    .map((d) => ({
      start: toMinutes(d.gioBatDau),
      end: toMinutes(d.gioKetThuc),
    }))
    .sort((a, b) => a.start - b.start);

  const slots = [];
  let current = start;

  for (const dat of datMins) {
    if (dat.start > current) {
      slots.push({
        gioBatDau: toTime(current),
        gioKetThuc: toTime(dat.start),
        trangThai: "trong",
      });
    }
    current = Math.max(current, dat.end);
  }

  if (current < end) {
    slots.push({
      gioBatDau: toTime(current),
      gioKetThuc: toTime(end),
      trangThai: "trong",
    });
  }

  return slots;
};

const Home = () => {
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);
  const [ngayChon, setNgayChon] = useState(() =>
    new Date().toISOString().split("T")[0]
  );
  const [danhSachSan, setDanhSachSan] = useState([]);

  // Load d·ªØ li·ªáu t·ª´ localStorage khi m·ªü web
  useEffect(() => {
    const ds = LAY_DATA_LS();
    if (ds.length > 0) {
      setDanhSachSan(ds);
    } else {
      // N·∫øu l·∫ßn ƒë·∫ßu => t·∫°o m·∫∑c ƒë·ªãnh
      setDanhSachSan([
        { ten: "S√¢n 1", giaThue: 70000, daDat: [] },
        { ten: "S√¢n 2", giaThue: 80000, daDat: [] },
      ]);
    }
  }, []);

  // Save v√†o localStorage m·ªói khi thay ƒë·ªïi
  useEffect(() => {
    LUU_DATA_LS(danhSachSan);
  }, [danhSachSan]);

  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot });
    setPopup(true);
  };

  const handleDatSan = (data) => {
    const sanIndex = danhSachSan.findIndex((s) => s.ten === data.san.ten);
    if (sanIndex === -1) return;

    const capNhat = [...danhSachSan];
    capNhat[sanIndex].daDat.push({
      gioBatDau: data.gioBatDau,
      gioKetThuc: data.gioKetThuc,
    });

    setDanhSachSan(capNhat);
    setPopup(false);
    alert("‚úÖ ƒê·∫∑t s√¢n th√†nh c√¥ng!");
  };

  const danhSachSanRender = danhSachSan.map((san) => ({
    ...san,
    thoiGian: tinhSlotTrong(san.daDat),
  }));

  return (
    <div className="p-6 max-w-4xl mx-auto bg-blur rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">ƒê·∫∑t S√¢n Victor üè∏</h1>

      <label className="block font-medium mb-2">Ch·ªçn ng√†y:</label>
      <input
        type="date"
        className="border p-2 mb-4 w-full"
        value={ngayChon}
        onChange={(e) => setNgayChon(e.target.value)}
      />

      <SanGrid
        danhSachSan={danhSachSanRender}
        ngayChon={ngayChon}
        onClickSlot={handleClickSlot}
      />

      <FooterMap />

      {popup && selected && (
        <PopupDatSan
          san={selected.san}
          slot={selected.slot}
          ngayMacDinh={ngayChon}
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
