import React, { useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

const dummyData = [
  {
    ten: "S√¢n 1",
    giaThue: 70000,
    thoiGian: Array.from({ length: 17 }, (_, i) => {
      const start = 6 + i;
      return {
        gioBatDau: `${start.toString().padStart(2, "0")}:00`,
        gioKetThuc: `${(start + 1).toString().padStart(2, "0")}:00`,
        trangThai: "trong", // ho·∫∑c "ban"
      };
    }),
  },
  {
    ten: "S√¢n 2",
    giaThue: 80000,
    thoiGian: Array.from({ length: 17 }, (_, i) => {
      const start = 6 + i;
      return {
        gioBatDau: `${start.toString().padStart(2, "0")}:00`,
        gioKetThuc: `${(start + 1).toString().padStart(2, "0")}:00`,
        trangThai: "trong",
      };
    }),
  },
];

const Home = () => {
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);
  const [ngayChon, setNgayChon] = useState(() =>
    new Date().toISOString().split("T")[0]
  );

  // Khi click 1 slot tr·ªëng trong s√¢n
  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot });
    setPopup(true);
  };

  // X·ª≠ l√Ω sau khi ƒë·∫∑t s√¢n th√†nh c√¥ng
  const handleDatSan = (data) => {
    console.log("‚úÖ ƒê·∫∑t s√¢n th√†nh c√¥ng:", data);
    setPopup(false);
    alert("üéâ ƒê·∫∑t s√¢n th√†nh c√¥ng!");
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-blur rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">
        ƒê·∫∑t S√¢n C·∫ßu L√¥ng Victor üè∏
      </h1>

      <label className="block font-medium mb-2">Ch·ªçn ng√†y:</label>
      <input
        type="date"
        className="border p-2 mb-4 w-full"
        value={ngayChon}
        min={new Date().toISOString().split("T")[0]}
        max={new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)
          .toISOString()
          .split("T")[0]}
        onChange={(e) => setNgayChon(e.target.value)}
      />

      {/* S∆° ƒë·ªì c√°c s√¢n */}
      <SanGrid
        danhSachSan={dummyData}
        ngayChon={ngayChon}
        onClickSlot={handleClickSlot}
      />

      {/* B·∫£n ƒë·ªì Google Maps */}
      <FooterMap />

      {/* Popup ƒë·∫∑t s√¢n */}
      {popup && selected && (
        <PopupDatSan
          san={selected?.san}
          slot={selected?.slot}
          ngayMacDinh={ngayChon} // ‚úÖ Truy·ªÅn ng√†y sang popup
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
