import React, { useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

const dummyData = [
  {
    ten: "Sân 1",
    giaThue: 70000,
    thoiGian: Array.from({ length: 17 }, (_, i) => {
      const start = 6 + i;
      return {
        gioBatDau: `${start.toString().padStart(2, "0")}:00`,
        gioKetThuc: `${(start + 1).toString().padStart(2, "0")}:00`,
        trangThai: "trong",
      };
    }),
  },
  {
    ten: "Sân 2",
    giaThue: 80000,
    thoiGian: Array.from({ length: 17 }, (_, i) => {
      const start = 6 + i;
      return {
        gioBatDau: `${start.toString().padStart(2, "0")}:00`,
        gioKetThuc: `${(start + 1).toString().padStart(2, "0")}:00`,
        trangThai: "trong",
      };
    }),
  },
];

const Home = () => {
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);
  const [ngayChon, setNgayChon] = useState(() => new Date().toISOString().split("T")[0]);

  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot, ngay: ngayChon }); // ✅ truyền ngày chọn vào popup
    setPopup(true);
  };

  const handleDatSan = (data) => {
    console.log("Đặt sân:", data);
    setPopup(false);
    alert("Đặt sân thành công (demo)!");
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-blur rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4">Đặt Sân Cầu Lông Victor</h1>

      <label className="block font-medium mb-2">Chọn ngày:</label>
      <input
        type="date"
        className="border p-2 mb-4"
        value={ngayChon}
        min={new Date().toISOString().split("T")[0]}
        max={new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split("T")[0]}
        onChange={(e) => setNgayChon(e.target.value)}
      />

      <SanGrid
        danhSachSan={dummyData}
        onClickSlot={handleClickSlot}
        ngayChon={ngayChon}
      />

      <FooterMap />

      {popup && selected && (
        <PopupDatSan
          san={selected.san}
          slot={selected.slot}
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
