  // üß† T√≠nh gi·ªù k·∫øt th√∫c m·ªói khi thay ƒë·ªïi gi·ªù b·∫Øt ƒë·∫ßu ho·∫∑c th·ªùi l∆∞·ª£ng
  useEffect(() => {
    const [h, m] = gioBatDau.split(":").map(Number);
    const ketThucGio = h + thoiLuong;
    if (ketThucGio <= 23) {
      setGioKetThuc(`${ketThucGio.toString().padStart(2, "0")}:00`);
    } else {
      setGioKetThuc("Kh√¥ng h·ª£p l·ªá");
    }
