    if (gioKetThuc === "Kh√¥ng h·ª£p l·ªá") {
      return alert("Gi·ªù k·∫øt th√∫c v∆∞·ª£t qu√° 23:00. Vui l√≤ng ch·ªçn l·∫°i.");
