import React, { useState } from "react";
import SanGrid from "../components/SanGrid";
import PopupDatSan from "../components/PopupDatSan";
import FooterMap from "../components/FooterMap";

// ‚è± Gi·ªù b·∫Øt ƒë·∫ßu v√† k·∫øt th√∫c trong ng√†y
const GIO_MO = 6;
const GIO_DONG = 23;

const danhSachSanGoc = [
  {
    ten: "S√¢n 1",
    giaThue: 70000,
    // C√°c khung gi·ªù ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t
    daDat: [
      {
        gioBatDau: "06:00",
        gioKetThuc: "07:00",
      },
    ],
  },
  {
    ten: "S√¢n 2",
    giaThue: 80000,
    daDat: [], // s√¢n n√†y ch∆∞a c√≥ ai ƒë·∫∑t
  },
];

// üîß H√†m t√≠nh slot tr·ªëng t·ª´ c√°c slot ƒë√£ ƒë·∫∑t
const tinhSlotTrong = (daDat) => {
  const toMinutes = (time) => {
    const [h, m] = time.split(":").map(Number);
    return h * 60 + m;
  };

  const toTime = (minutes) => {
    const h = Math.floor(minutes / 60)
      .toString()
      .padStart(2, "0");
    return `${h}:00`;
  };

  const start = GIO_MO * 60;
  const end = GIO_DONG * 60;

  const datMins = daDat
    .map((d) => ({
      start: toMinutes(d.gioBatDau),
      end: toMinutes(d.gioKetThuc),
    }))
    .sort((a, b) => a.start - b.start);

  const slots = [];
  let current = start;

  for (const dat of datMins) {
    if (dat.start > current) {
      slots.push({
        gioBatDau: toTime(current),
        gioKetThuc: toTime(dat.start),
        trangThai: "trong",
      });
    }
    current = Math.max(current, dat.end);
  }

  if (current < end) {
    slots.push({
      gioBatDau: toTime(current),
      gioKetThuc: toTime(end),
      trangThai: "trong",
    });
  }

  return slots;
};

const Home = () => {
  const [popup, setPopup] = useState(false);
  const [selected, setSelected] = useState(null);
  const [ngayChon, setNgayChon] = useState(() =>
    new Date().toISOString().split("T")[0]
  );

  // X·ª≠ l√Ω slot tr·ªëng cho t·ª´ng s√¢n
  const danhSachSan = danhSachSanGoc.map((san) => ({
    ...san,
    thoiGian: tinhSlotTrong(san.daDat),
  }));

  const handleClickSlot = (san, slot) => {
    setSelected({ san, slot });
    setPopup(true);
  };

  const handleDatSan = (data) => {
    console.log("‚úÖ ƒê·∫∑t s√¢n:", data);
    setPopup(false);
    alert("üéâ ƒê·∫∑t s√¢n th√†nh c√¥ng!");
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-blur rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">
        ƒê·∫∑t S√¢n C·∫ßu L√¥ng Victor üè∏
      </h1>

      <label className="block font-medium mb-2">Ch·ªçn ng√†y:</label>
      <input
        type="date"
        className="border p-2 mb-4 w-full"
        value={ngayChon}
        min={new Date().toISOString().split("T")[0]}
        max={new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)
          .toISOString()
          .split("T")[0]}
        onChange={(e) => setNgayChon(e.target.value)}
      />

      {/* S∆° ƒë·ªì c√°c s√¢n */}
      <SanGrid
        danhSachSan={danhSachSan}
        ngayChon={ngayChon}
        onClickSlot={handleClickSlot}
      />

      {/* B·∫£n ƒë·ªì */}
      <FooterMap />

      {/* Popup ƒë·∫∑t s√¢n */}
      {popup && selected && (
        <PopupDatSan
          san={selected?.san}
          slot={selected?.slot}
          ngayMacDinh={ngayChon}
          onClose={() => setPopup(false)}
          onDat={handleDatSan}
        />
      )}
    </div>
  );
};

export default Home;
