import React from "react";

const SanGrid = ({ danhSachSan, onClickSlot }) => {
  const now = new Date();

  // Tính giờ tối thiểu có thể đặt (dựa theo thời gian thực)
  let gioToiThieu = 6;
  const minutesNow = now.getMinutes();
  const hoursNow = now.getHours();

  if (minutesNow >= 15) {
    gioToiThieu = hoursNow + 1;
  } else {
    gioToiThieu = hoursNow;
  }

  if (gioToiThieu < 6) gioToiThieu = 6;
  if (gioToiThieu > 22) gioToiThieu = 23; // không cho đặt sau 23h

  return (
    <div className="grid grid-cols-2 gap-4">
      {danhSachSan.map((san) =>
        san.thoiGian.map((slot, index) => {
          const [startHour] = slot.gioBatDau.split(":").map(Number);

          // Chỉ hiển thị nếu giờ bắt đầu lớn hơn hoặc bằng giờ tối thiểu
          const hopLe = startHour >= gioToiThieu;

          if (!hopLe) return null;

          return (
            <div
              key={`${san.ten}-${index}`}
              onClick={() => slot.trangThai === "trong" && onClickSlot(san, slot)}
              className={`border p-2 rounded cursor-pointer text-center ${
                slot.trangThai === "trong"
                  ? "bg-white hover:bg-gray-100"
                  : "bg-gray-300 cursor-not-allowed"
              }`}
            >
              <p className="font-bold">{san.ten}</p>
              <p>
                {slot.gioBatDau} - {slot.gioKetThuc}
              </p>
            </div>
          );
        })
      )}
    </div>
  );
};

export default SanGrid;
